package Practice;

public class Practice_31 { // Returning a Value

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum;
		sum = oddOperation(5, 12);
		System.out.println(sum);
	}

	public static int oddOperation(int a, int b) {
		int sum = a + b;
		return sum;
	}
}
